document.getElementById("form").addEventListener("submit",function(r){r.preventDefault();for(var d=document.getElementById("titolAntic").value,i=document.getElementById("titol").value,s=document.getElementById("resum").value,m=document.getElementById("portada").value,n=[],a=document.getElementById("tags").selectedOptions,e=0;e<a.length;e++)n.push(a[e].value);for(var c=[],l=document.getElementById("director").selectedOptions,e=0;e<l.length;e++)c.push(l[e].value);var v=document.getElementById("valoracio").value,u={titolAntic:d,titol:i,resum:s,portada:m,tags:n,director:c,valoracio:v};fetch("http://imdb.test/api/movies/modify",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(u)}).then(t=>t.json()).then(t=>{document.getElementById("resposta").innerHTML="";for(var p in t.missatges){var o=document.createElement("p");o.textContent=t.missatges[p],t.isValid?o.style.color="green":o.style.color="red",document.getElementById("resposta").appendChild(o)}})});
