document.getElementById("form").addEventListener("submit",function(n){n.preventDefault();var r=document.getElementById("name").value,o=document.getElementById("birthdate").value,a={name:r,birthdate:o};fetch("http://imdb.test/api/directors/store",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(a)}).then(e=>e.json()).then(e=>{document.getElementById("resposta").innerHTML="";for(var d in e.missatges){var t=document.createElement("p");t.textContent=e.missatges[d],e.isValid?t.style.color="green":t.style.color="red",document.getElementById("resposta").appendChild(t)}}).catch(console.error)});
