document.getElementById("form").addEventListener("submit",function(t){t.preventDefault(),fetch("http://imdb.test/api/tags/store",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({tag:document.getElementById("tag").value})}).then(e=>e.json()).then(e=>{document.getElementById("resposta").textContent=e.message,e.isValid?document.getElementById("resposta").style.color="green":document.getElementById("resposta").style.color="red"})});
